import { Scene, PerspectiveCamera, DirectionalLight, Mesh, MeshBasicMaterial, Vector3, PCFSoftShadowMap, Color, Group, BoxGeometry, CapsuleGeometry, SphereGeometry, CircleGeometry, ConeGeometry, Clock, WebGLRenderer, TextureLoader, BufferGeometry, BufferAttribute, CylinderGeometry, MeshPhongMaterial, DoubleSide, AmbientLight, PointLight, PlaneGeometry } from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls.js';
import GUI from 'lil-gui';

class WebGLApp {
  constructor(parent) {
    this.animate = () => {
      this.renderer.render(this.scene, this.camera);
      requestAnimationFrame(this.animate);
      this.controls.update();
    };

    this.resize = (width, height) => {
      this.camera.aspect = width / height;
      this.camera.updateProjectionMatrix();
      this.renderer.setSize(width, height);
    };

    this.addLights = () => {
      this.createAmbientLight();
      this.createPointLight();
    };

    this.createAmbientLight = () => {
      this.ambientLight = new AmbientLight(0x999999, 1);
      this.scene.add(this.ambientLight);
    };

    this.createPointLight = () => {
      this.light = new PointLight(0xffffff, 0.3, 0);
      this.light.castShadow = true;
      this.light.shadow.mapSize.width = 5000;
      this.light.shadow.mapSize.height = 5000;
      this.light.position.set(0, 20, -5);
      this.scene.add(this.light);
      const light = new PointLight(0xffffff, 0.2, 0);
      light.castShadow = false;
      light.position.set(0, 30, 50);
      this.scene.add(light);
    };

    this.makeScene = () => {
      this.addLights(); // const gui = new GUI;

      /*
      In pseudocode, that's something like:
       // starting point x = 500 y = 100
      begin_curve()
      for (n in [0..num_steps]) {
          draw_vertex(x, y)
          x_offset = x - left_x     y_offset = y - top_y
          column_index = int(x_offset / resolution)     row_index = int(y_offset / resolution)
          // NOTE: normally you want to check the bounds here     grid_angle = grid[column_index][row_index]
          x_step = step_length * cos(grid_angle)     y_step = step_length * sin(grid_angle)
          x = x + x_step     y = y + y_step }
      end_curve()
      */

      var res = 10;
      const width = window.innerWidth;
      const height = window.innerHeight;
      var value;

      for (var x = 0; x < width; x += res) {
        for (var y = 0; y < height; y += res) {
          value = (x + y) * 0.01 * Math.PI * 2;
          var cube = new Mesh(new BoxGeometry(10, 10, 1), new MeshBasicMaterial({
            color: 0x00ff00
          }));
          cube.position.set(x, y, 1);
          this.scene.add(cube);
        }
      } // //scene
      // gui
      // .addColor(this.params, 'backgroundColor')
      // .onChange(()=>{
      //     this.scene.background.set(this.params.backgroundColor)
      // })
      // gui.close()
      // this.scene.add(gui)

    };

    this.makeGUI = (gui, folderName, obj) => {
      //gui face
      const objGUI = gui.addFolder(folderName);
      objGUI.add(obj.position, 'x').min(-10).max(10).step(0.01);
      objGUI.add(obj.position, 'y').min(-10).max(10).step(0.01);
      objGUI.add(obj.position, 'z').min(-Math.PI).max(Math.PI).step(0.01);
      objGUI.add(obj.rotation, 'x').min(-Math.PI).max(Math.PI).step(0.01).name('rotX');
      objGUI.add(obj.rotation, 'y').min(-Math.PI).max(Math.PI).step(0.01).name('rotY');
      objGUI.add(obj.rotation, 'z').min(-Math.PI).max(Math.PI).step(0.01).name('rotZ');
      objGUI.addColor(this.params, 'color').onChange(() => {
        obj.material.color.set(this.params.color);
      });
      objGUI.close();
    };

    //the main function
    this.params = {
      color: 0xff0000,
      backgroundColor: 0xe2f0f9
    };
    const aspect = window.innerWidth / window.innerHeight; // const clock = new Clock()
    //set up scene

    this.scene = new Scene();
    this.scene.background = new Color(this.params.backgroundColor);
    this.camera = new PerspectiveCamera(75, aspect, 0.1, 1000);
    this.renderer = new WebGLRenderer({
      antialias: true
    });
    this.renderer.setSize(window.innerWidth, window.innerHeight);
    this.renderer.shadowMap.enabled = true;
    this.renderer.shadowMap.type = PCFSoftShadowMap;
    this.scene.add(this.camera);
    this.camera.position.z = 10; // controls

    this.controls = new OrbitControls(this.camera, this.renderer.domElement); // this.resize(window.innerWidth, window.innerHeight)

    this.cursor = {
      x: 0,
      y: 0
    };
    parent.appendChild(this.renderer.domElement);
    this.makeScene();
    this.animate();
  }

}

export default WebGLApp;

